import { useState } from "react";
//import HomePage from "../HomePage"
import styled from "styled-components";



function Login() {
  const [name, setName] = useState("");
  const [password, setPassword] = useState("")
    const newLocal = useState(false);
  //Needs to be in the login page
  const [loggedIn, setLoggedIn] = newLocal


  function handleNameChange(event){
      setName(event.target.value);
  }

 function handleChange(event) {
    setPassword(event.target.value);
  }


  function handleSubmit(event) {
    // Stop the page from refreshing as the form will try and submit and refresh by default



    event.preventDefault();
    //onSubmitClick(password);
    if(password === "Koder"){
        console.log("correct password");
        setLoggedIn(true);

    }else{
        console.log("wrong password")
    }
  }

  return (



    <div>
    <h1>Login</h1>
    <LoginForm>
    <form value={loggedIn}>
    <StyledInput onChange={handleNameChange} value={name} placeholder="Tell us your name.."/>
      <StyledInput onChange={handleChange} value={password} placeholder="Enter the password.." />
      <StyledButton onClick={handleSubmit}>Login</StyledButton>
    </form>
    </LoginForm>



    {/* {loggedIn ? <Navigate to="/homepage"/> : <Navigate to="/"/>} */}

</div>

  );
}

export default Login;

const StyledButton = styled.button`
background-color: #19647E;
color: #FFFCFF ;
text-align: center;
text-justify: center;
border-radius: 10%;

left: 50%;
margin-top:25%;
margin-bottom:25%;
padding: 12px 50px 12px 50px;
width: 200px;
height: 10px;
`

const StyledInput = styled.input`
border-color: #9DD9D2;
border-radius: 10%;
margin-top: 25%;
padding: 12px 20px;
width: 200px;
height: 10px;
 `

const LoginForm = styled.section`
position: center;
margin: auto;
padding: 20px 200px 20px 200px; 
width: 300px;
height: 400px;
border: 1px solid black;
box-shadow: 5px 10px;
` 
const StyledTitle = styled.h1`
padding-bottom: 20px`
